<script type="text/javascript">
  function openSideBar() {
    document.querySelector("#menuSideBar").classList.add("expanded");
    if (!document.querySelector("#CloseIcon").classList.contains("open")) {
      document.querySelector("#CloseIcon").classList.add("open");
    }
  }
  function closeSideBar(){
    document.querySelector("#menuSideBar").classList.remove("expanded");
    if (!document.querySelector("#OpenIcon").classList.contains("open")) {
      document.querySelector("#CloseIcon").classList.remove("open");
    }
  }
</script>
